import React from 'react';
import logo from './logo.svg';
import './App.css';
import { GraphVisualizer, IGraphView, Template, Toolbar, ToolButtonList, store, IMatrixView, INGraphsView, graphActionCreators, StudentMark, init, graphModel, Console, State } from "graphlabs.core.template";
