import 'graphlabs.core.template/dist/main.css';

import { Component, SFC} from 'react';
import { init1, graphModel1, init2, graphModel2, initres, graphModelres } from './ForMyGraphModel';

class App extends Template {
    protected message_0 = "";
    protected num_0 = 0;

    public state = {
        status: store.getState().app.status,
    };

    componentWillMount() {
        let graph: IGraph<IVertex, IEdge>;

        graph = GraphGenerator.generate(0);
        init(graph);

        let graph1: IGraph<IVertex, IEdge>;
        graph1 = this.GetNewRandomGraph(5);
        init1(graph1);

        let graph2: IGraph<IVertex, IEdge>;
        graph2 = this.GetNewRandomGraphForThatOne(5,graph1);
        init2(graph2);

        let graphres = this.ChooseTask();
        initres(graphres);
        //this.scc_count = SccBuilder.findComponents(graphres).length;
    }


        //Для меня в будующем: проблема невозможности удать ребро заключается в том, что я создаю два других, тем саммы ломая себе всё.
        //Это нужно проверить и внести определённые правки. Также нужно узнать как мой модуль добавить на сайт, а не только на гитхаб.


    protected ChooseTask(){
        this.num_0 = Math.round(Math.random() * 100)%7;
        switch (this.num_0) {
            case 0:
                this.message_0 = "Постройте граф, являющийся результатом объединения двух графов.";
                return this.Uni(graphModel1,graphModel2);
            case 1:
                this.message_0 = "Постройте граф, являющийся результатом соединения двух графов.";
                return this.Joint(graphModel1,graphModel2);
            case 2:
                this.message_0 = "Постройте граф, являющийся результатом произведения двух графов.";
                return this.Product(graphModel1,graphModel2);
            case 3:
                this.message_0 = "Постройте граф, являющийся результатом композиции двух графов.";
                return this.Composition(graphModel1,graphModel2);
            case 4:
                this.message_0 = "Постройте граф, являющийся результатом объединения по Зыкову двух графов."; // Объединение по Зыкову
                return this.Uni_Z(graphModel1,graphModel2);
            case 5:
                this.message_0 = "Постройте граф, являющийся результатом пересечения двух графов."; // Пересечение
                return this.Cross(graphModel1,graphModel2);
            case 6:
                this.message_0 = "Постройте граф, являющийся дополнением графа."; // Дополнение
                return this.Addition(graphModel1);
            case 7:
                this.message_0 = "Постройте граф, являющийся результатом декартового произведения двух графов."; // Декартово произведение
                return this.Cartesian_Product(graphModel1,graphModel2);
            default:
                this.message_0 = "Постройте граф, являющийся результатом объединения двух графов.";
                return this.Uni(graphModel1,graphModel2);
        }
    }

    protected GetNewRandomGraph (num:number){ // рандомный граф
        let graph: IGraph<IVertex, IEdge>;
        graph = GraphGenerator.generate(0);
        var arr = [];
        for(var i = 0; i<num; i++ ){
            arr.push(Math.round(Math.random()));
            if(arr[i]===1){
                let vert = new Vertex(`${i}`);
                graph.addVertex(vert);
            }
        }
        for(var i=0;i<num-1;i++){
            if(arr[i]===1) {
                for(var j = i+1;j<num;j++){
                    if(arr[j]===1 && Math.random()>0.45 ){ // 55% chance what is edge i+j
                        let edge = new Edge(graph.getVertex(`${i}`)[0],graph.getVertex(`${j}`)[0],`${i}+${j}`);
                        graph.addEdge(edge);
                    }
                }
            }
        }
        return graph;
    }

